INSERT INTO `services` VALUES
('1','Tổ chức tiệc cưới','Wedding Services','wedding-service','event','Aurora Hotel Plaza tự hào là địa điểm tổ chức tiệc cưới hàng đầu tại Đồng Nai với sảnh tiệc sang trọng 500m², sức chứa lên đến 800 khách. Đội ngũ chuyên nghiệp với hơn 500 tiệc cưới thành công.','Complete wedding planning and hosting services with elegant venues.','Sảnh tiệc 500m², sức chứa 800 khách, dịch vụ trọn gói','Elegant wedding venues and planning','celebration','0.00','Liên hệ','assets/img/post/wedding/tiec-cuoi-tai-aurora-1.jpg',NULL,'1','1','1','2025-11-19 01:34:50','2025-12-16 23:52:56'),
('2','Tổ chức hội nghị','Conference & Events','conference-service','event','Phòng hội nghị hiện đại với 5 phòng họp đa năng, sức chứa từ 20-300 người. Trang bị đầy đủ thiết bị hiện đại, WiFi tốc độ cao, dịch vụ coffee break và buffet.','Professional conference rooms and event spaces with full equipment.','Phòng họp hiện đại, thiết bị đầy đủ, WiFi tốc độ cao','Professional meeting spaces','meeting_room','5000000.00','VNĐ','assets/img/service/meet/hoi-nghi-aurora-5.jpg',NULL,'1','1','2','2025-11-19 01:34:50','2025-12-21 15:21:41'),
